// Function to load HTML content dynamically
function loadHTML(file, elementId) {
    fetch(file)
        .then(response => response.text())
        .then(html => {
            document.getElementById(elementId).innerHTML = html;
        })
        .catch(error => {
            console.error('Error loading the HTML:', error);
        });
}

// Load header and footer on DOMContentLoaded
document.addEventListener('DOMContentLoaded', function () {
    loadHTML('partials/header.html', 'header');  // Load header
    loadHTML('partials/footer.html', 'footer');  // Load footer
});
