
// Load header and footer on DOMContentLoaded
document.addEventListener('DOMContentLoaded', function () {
    loadFromIndex('index.html', 'header', '#header');
    loadFromIndex('index.html', 'footer', '#footer');
   
});

// Function to load HTML content dynamically
function loadFromIndex(url, elementId, selector) {
    fetch(url)
        .then(response => response.text())
        .then(data => {
            const parser = new DOMParser();
            const doc = parser.parseFromString(data, 'text/html');
            const content = doc.querySelector(selector).innerHTML;
            document.getElementById(elementId).innerHTML = content;
        })
        .catch(error => console.log('Error loading HTML:', error));
}



